from random import getrandbits

class Solution:
    def longestValidParentheses(self, s: str) -> int:
        s = s.lstrip(')')
        s = s.rstrip('(')
        s = [1 if i == '(' else -1 for i in s]
        c = s.count(1)
        d = len(s) - c
        b = c < d
        l, r = 0, len(s) - 1
        ans = cur_sum = 0
        cur_val = i = l if b else r
        while l < r:
            cur_sum += s[i]
            if cur_sum >= 0:
                cur_val += 1
            if cur_sum <= 0:
                ans = max(ans, cur_val)
            if cur_sum < 0:
                while s[i] == -1:
                    i += 1
                if b:
                    l = i
                else:
                    r = i
                cur_sum = cur_val = 0
                c = s[l:r+1].count(1)
                d = len(s[l:r+1]) - c
                b = c < d
            i += 1 if b else -1
        return ans

s = ''
for i in range(getrandbits(5)):
    if getrandbits(1):
        s += ')'
    else:
        s += '('
